(1886, 348, '_main_banner_title', 'field_5c4b395b2d3ab'),
(1887, 348, 'main_banner_text', 'In a region replete with talent and resources, Uplanders—from students to visionaries, artisans to entrepreneurs—are competitive and thriving here and worldwide. Uplands families, a dedicated workforce, and a tide of trailblazers call our 11 counties home: Home of industry and determination, innovation and creativity, good friends and reliable neighbors. Our home is Uplands Made.'),
(1888, 348, '_main_banner_text', 'field_5c4b41ca9ac96'),
(1889, 348, 'main_title', 'fun fun fun'),
