(4325, 587, '_section_three_image_three', 'field_5c53200d28f7f'),
(4326, 587, 'section_three_a', 'Interstate 69'),
(4327, 587, '_section_three_a', 'field_5c53208d28f80'),
(4328, 587, 'section_three_b', 'Trails & Trestles'),
(4329, 587, '_section_three_b', 'field_5c5320c428f81'),
(4330, 587, 'section_three_c', 'Bridges & Byways'),
(4331, 587, '_section_three_c', 'field_5c5320ca28f82'),
(4332, 587, 'section_three_a_paragraph', 'Venture along I-69, the Midwestern highway that will eventually stretch from Canada to Mexico—with the Uplands neatly situated in the center. This breathtaking drive through rolling hills, robust farmland, and thriving cities will connect three countries and eight states to the heart of the Uplands.'),
(4333, 587, '_section_three_a_paragraph', 'field_5c5320d028f83'),
(4334, 587, 'section_three_b_paragraph', 'Play and explore on over 75 hiking trails. Pump your tires and bike the 48-mile Union Pass Road, lace up your hiking boots for a day in Hoosier National Forest, or pay a visit to Greene County’s Tulip Trestle.'),
(4335, 587, '_section_three_b_paragraph', 'field_5c5320ed28f84'),
(4336, 587, 'section_three_c_paragraph', 'Visit Bean Blossom Covered Bridge, tour the Brown County motorcycle path, or experience the beauty of the 25-mile French Lick Scenic Railway between French Lick and Jasper. Curiosity and amazement will be your guide.'),
(4337, 587, '_section_three_c_paragraph', 'field_5c5320f728f85'),
