(5191, 643, '_section_one_title', 'field_5c53137265864'),
(5192, 643, 'section_one_paragraph', 'The Uplands are perfectly located for every whimsical excursion, studious retreat, and corporate collaboration in the Midwest. We combine Hoosier hospitality with world-class access.'),
(5193, 643, '_section_one_paragraph', 'field_5c5313e565865'),
(5194, 643, 'section_two_title', 'Getting Here'),
(5195, 643, '_section_two_title', 'field_5c53171187f21'),
(5196, 643, 'section_two_paragraph', 'At the intersection between Indianapolis, Cincinnati, and St. Louis, the rolling hills and forests of the Uplands beckon any roadfarers to pause and admire this beautiful place we call home.'),
(5197, 643, '_section_two_paragraph', 'field_5c53178a87f22'),
(5198, 643, 'section_two_image_one', '249'),
