(6020, 694, '_featured_event_link_text', 'field_5c958296af9e5'),
(6021, 695, 'section_one_title', 'Uplands to Anywhere'),
(6022, 695, '_section_one_title', 'field_5c53137265864'),
(6023, 695, 'section_one_paragraph', 'The Uplands are perfectly located for every whimsical excursion, studious retreat, and corporate collaboration in the Midwest. We combine Hoosier hospitality with world-class access.'),
(6024, 695, '_section_one_paragraph', 'field_5c5313e565865'),
(6025, 695, 'section_two_title', 'Getting Here'),
(6026, 695, '_section_two_title', 'field_5c53171187f21'),
(6027, 695, 'section_two_paragraph', 'At the intersection between Indianapolis, Cincinnati, and St. Louis, the rolling hills and forests of the Uplands beckon any roadfarers to pause and admire this beautiful place we call home.'),
